import React from 'react';
import { Link } from 'react-router-dom';

interface Props {
  handleCategChange: (id: string) => void; // Define the type of handleCategChange
}

function BarMenu({ handleCategChange }: Props) {
  const openMenu = () => {
    const sidebar = document.querySelector(".sidebar");
    if (sidebar) {
      sidebar.classList.add("open");
    }
  }

  const closeMenu = () => {
    const sidebar = document.querySelector(".sidebar");
    if (sidebar) {
      sidebar.classList.remove("open");
    }
  }

  const categories = ["Food", "Pet Care", "Toy", "All"];

  return (
    <aside className="sidebar">
      <div id="hmenu-customer-profile-link" data-nav-ref="nav_em_hd_re_signin">
        <div id="hmenu-customer-profile">
          <div className="customer-profile"> <span className="profile-icon"><i className="far fa-user-circle"></i></span> Hello!</div>
          <button className="sidebar-close-button" onClick={closeMenu}>X</button>
        </div>
      </div>
      <div className="hmenu-header">Shop by Category</div>
      <ul className="hmenu-content">
        {categories.map((category) => (
          <li key={category}>
            <button id={category} onClick={(e) => handleCategChange((e.target as HTMLButtonElement).id)} className="hmenu-item" data-menu-id="1">
              {category}
              <i className="fas fa-chevron-right hmenu-icon"></i>
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

export default BarMenu;
