import { useState, useContext } from "react";
import CartContext from "../../context/cartContext/cartContext";
import {
  CardNumberElement,
  CardCvcElement,
  CardExpiryElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";

export const PaymentForm = () => {
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState<string | undefined>();
  const [successMessage, setSuccessMessage] = useState<string | undefined>();
  const [billedAmount, setBilledAmount] = useState<string | undefined>();
  const [paymentMethod, setPaymentMethod] = useState<"card" | "upi">("card");
  const [upiId, setUpiId] = useState("");

  const stripe = useStripe();
  const elements = useElements();

  const { total, handleCheckout } = useContext(CartContext);

  const stripeTotal = total * 100;

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError(undefined);

    if (!stripe || !elements) {
      return;
    }

    if (paymentMethod === "card") {
      const card = elements.getElement(CardNumberElement);

      if (card == null) {
        return;
      }

      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card,
      });

      if (!error) {
        try {
          const { id } = paymentMethod;
          const transaction = { amount: stripeTotal, id };
          const response = await fetch("/payment/stripe", {
            method: "POST",
            headers: { "Content-type": "application/json" },
            body: JSON.stringify(transaction),
          });

          const data = await response.json();
          if (data.success) {
            setSuccessMessage("Payment successful.");
            setSuccess(true);
            handleCheckout();
            setBilledAmount(
              (data.amount / 100).toLocaleString("en-US", {
                style: "currency",
                currency: "USD",
              })
            );
          }
          else 
          {
            setError(data.message);
            setSuccess(false);
          }
        } 
        catch (error) 
        {
          console.log("Error", error);
        }
      } else {
        setError(error.message);
      }
    } else if (paymentMethod === "upi") {
      alert("Check your mobile to pay money. If not, please try again.");
      window.location.href = "/cart";
    }
  };

  return (
    <>
      {!success ? (
        <div className="payment-form-container">
          <form onSubmit={handleSubmit} className="payment-form">
            <div className="form-header">
              <h2>Basket Total: <span className="basket-total">${total}</span></h2>
              <div className="payment-methods">
                <label htmlFor="type1" className="payment-option">
                  <input
                    type="radio"
                    className="form-radio"
                    name="type"
                    id="type1"
                    defaultChecked
                    onClick={() => setPaymentMethod("card")}
                  />
                  <img
                    src="https://leadershipmemphis.org/wp-content/uploads/2020/08/780370.png"
                    alt="Credit card logos."
                    className="payment-image"
                  />
                </label>
                <label htmlFor="type2" className="payment-option">
                  <input
                    type="radio"
                    className="form-radio"
                    name="type"
                    id="type2"
                    onClick={() => setPaymentMethod("upi")}
                  />
                  <img
                    src="/uploads/netbanking.jpeg"
                    alt="Credit card logos."
                    className="payment-image"
                  />
                </label>
              </div>
            </div>
            <div className="payment-info">
              {paymentMethod === "card" && (
                <div>
                  <div className="text-gray-600 font-bold text-lg mb-1">
                    Payment Info
                  </div>
                  {error && <div className="error-message">{error}</div>}
                  {successMessage && <div className="success-message">{successMessage}</div>}
                  <div className="form-group">
                    <label htmlFor="name" className="form-label">Name on card</label>
                    <input
                      id="name"
                      className="form-input"
                      placeholder="John Smith"
                      type="text"
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="card-number" className="form-label">Card number</label>
                    <CardNumberElement className="card-input" />
                    <p className="card-hint">For checkout use mock card # 4242 4242 4242 4242</p>
                  </div>
                  <div className="form-group">
                    <label htmlFor="expiry" className="form-label">Expiration date</label>
                    <CardExpiryElement className="card-input" />
                    <p className="card-hint">Mock exp. <span className="font-medium">future</span></p>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cvc" className="form-label">Security code</label>
                    <CardCvcElement className="card-input" />
                    <p className="card-hint">Mock CVC <span className="font-medium"> 424</span></p>
                  </div>
                </div>
              )}
              {paymentMethod === "upi" && (
                <div>
                  <div className="text-gray-600 font-bold text-lg mb-1">
                    UPI/ID Info
                  </div>
                  <div className="form-group">
                    <label htmlFor="upi-id" className="form-label">UPI/ID</label>
                    <input
                      id="upi-id"
                      className="form-input"
                      placeholder="yourname@upi"
                      type="text"
                      value={upiId}
                      onChange={(e) => setUpiId(e.target.value)}
                    />
                  </div>
                </div>
              )}
            </div>
            <button className="payment-button">
              <i className="mdi mdi-lock-outline mr-1"></i> Pay Now
            </button>
          </form>
        </div>
      ) : (
        <div className="payment-success">
          <h2>Thank you for your patronage</h2>
          <h3 className="success-message">Payment of {billedAmount} successful.</h3>
        </div>
      )}
    </>
  );
};

// CSS styles
const styles = `
.payment-form-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 50px;
}

.payment-form {
  width: 80%;
  max-width: 600px;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
}

.form-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.payment-option {
  display: flex;
  align-items: center;
  cursor: pointer;
  margin-right: 10px;
}

.payment-option input[type="radio"] {
  margin-right: 5px;
}

.payment-image {
  width: 40px;
}

.payment-info {
  margin-top: 20px;
}

.error-message {
  color: red;
  margin-bottom: 10px;
}

.success-message {
  color: green;
  margin-bottom: 10px;
}

.form-group {
  margin-bottom: 15px;
}

.form-label {
  font-size: 14px;
  margin-bottom: 5px;
}

.form-input {
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.card-input {
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.card-hint {
  font-size: 12px;
  margin-top: 5px;
  color: #666;
}

.payment-button {
  margin-top: 20px;
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.payment-button:hover {
  background-color: #0056b3;
}
`;

export default PaymentForm;
