import { useState, ChangeEvent, FormEvent } from 'react';
import { Link } from 'react-router-dom';
import useAuth from '../../context/userContext/useAuth';

export const RegisterForm = () => {
  const { register, message, error, loading } = useAuth();
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState<string>('');

  const handlePasswordChange = (e: ChangeEvent<HTMLInputElement>) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    validatePassword(newPassword);
  };

  const validatePassword = (password: string) => {
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!passwordRegex.test(password)) {
      setPasswordError(
        'Password must contain at least 8 characters, including one uppercase letter, one lowercase letter, one number, and one special character.'
      );
    } else {
      setPasswordError('');
    }
  };

  const handleRegister = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const form = e.target as HTMLFormElement;
    const userData = {
      username: form[0].value,
      email: form[1].value,
      password: form[2].value,
    };

    register(userData);
  };

  return (
    <>
      {/* Your existing JSX */}
      <div className="mb-6">
        <input
          type="password"
          className={`form-control block w-full px-4 py-2 text-xl font-normal text-gray-700 bg-white bg-clip-padding border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none ${
            passwordError ? 'border-red-500' : ''
          }`}
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange}
          disabled={loading}
        />
        {passwordError && (
          <p className="text-sm text-red-500 mt-1">{passwordError}</p>
        )}
      </div>
      {/* Your existing JSX */}
      <button
        type="submit"
        className="inline-block focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-800
                  text-base
                  flex
                  rounded-lg
                  items-center
                  justify-center
                  leading-none
                  text-white
                  bg-slate-700
                  w-full
                  py-4
                  hover:bg-gray-900"
        data-mdb-ripple="true"
        data-mdb-ripple-color="light"
        disabled={loading || !!passwordError} // Converted to boolean using !! to ensure it's either true or false
      >
        {loading ? 'Registering...' : 'Register'}
      </button>
    </>
  );
};
